mapper.select:
select count(*) from user

    uid=#{value}
    nick=#{value}
    hp=#{value}
    email=#{value}

mapper.update:
update user

    nick=#{value}
    name=#{value}
    hp=#{value}
    email=#{value}

where uid=#{uid}

update user set
zip = #{zip},
addr1 = #{addr1},
